      setSearchKey(e.target.value)
    }
    return (
     <>
      <div className="searchbox">
        <input type="text" className="form-control" onChange={handleInput} placeholder="Enter surah name "/>
      </div>
      {/* <Playlist searchkey = {searchkey}/> */}
     </>
    )
}
export default Search
