import React, { useEffect, useState } from 'react'
import { useParams , Link } from 'react-router-dom';
import Card from '../components/Card';
import Search from '../components/Search';

export default function Playlist(props) {
    let [data , setData] = useState([]);
    let {readername} = useParams();

    let getData = ()=>{
        fetch(`http://localhost:3000/playlist/?readernameEN=${readername}`)
        .then((response)=> response.json()).
        then((result) => setData(result)) 
    }
    
    
    useEffect(()=>{
      getData();
    },[readername])
    if(data.length > 1){
      data.sort((a ,b) => a.surahnum- b.surahnum)
    }
    let handleChange = (e)=>{
     
      data.map((item)=>{
        let searchword = e.target.value;
        
        let fo = []
        // console.log(surahname.toLowerCase().includes(searchword.trim()))
        //  if(surahname){
          // console.log(item)
           console.log(data.find(function(current){
            let surahname = current.surahEN;
            console.log(surahname)
            if(surahname.toLowerCase().includes(searchword.trim()))
            fo.push(current)
          }))
          console.log(fo)
        //  }

      })
    }
  return (
    <>

    <div className='container'>
      <div className="searchbox">
        <input type="text" className="form-control" onChange={handleChange} placeholder="Enter surah name "/>
      </div>
      <div className='row'>
      
        {data.map((Item)=>{
            return(
                <div className='col-xl-3 pt-5' key={Item.surahEN}>
                  <Link to="/">
                    <Card data={Item}/>
                  </Link>
                </div>
            )
        })}
      </div>
    </div>
    </>
  )
}
