import React, { useEffect, useState } from 'react'
import { useParams , Link } from 'react-router-dom';
import Card from '../components/Card';
import Search from '../components/Search';

export default function Playlist(props) {
    let [data , setData] = useState([]);
    let {readername} = useParams();

    let getData = ()=>{
        fetch(`http://localhost:3000/playlist/?readernameEN=${readername}`)
        .then((response)=> response.json()).
        then((result) => setData(result)) 
    }
    data.sort((a ,b) => a.surahnum- b.surahnum)
    
    useEffect(()=>{
      getData();
    },[readername])
  return (
    <>
    <Search/>
    <div className='container'>
      <div className='row'>
      <div className="searchbox">
        <input type="text" className="form-control" placeholder="Enter surah name "/>
      </div>
    <div className='card'>
      <div className='card-img'>
        <div className='titleoverlay'>
          <h1>سورة {props.data.surahAR}</h1>
        </div>
        <img src={props.data.imgsrc} alt=""/>
      </div>
      <div className='card-body'>
        <div className='card-title'>
          <h3>{props.data.surahEN}</h3>
        </div>
      </div>
    </div>
        {data.map((Item)=>{
            return(
                <div className='col-xl-3 pt-5' key={Item.surahEN}>
                  <Link to="/">
                    <Card data={Item}/>
                  </Link>
                </div>
            )
        })}
      </div>
    </div>
    </>
  )
}
