import React ,{useState , useEffect} from 'react'
import { useParams } from 'react-router-dom';
import Audio from '../components/Audio';
import Slider from '../components/Slider';
import NotFoundPage from './NotFound';
export default function PlayAudio() {
  let {readername,surahname} = useParams();
  let [data, setData] = useState([]);
  useEffect(()=>getData(),[])

  let getData = ()=>{
    fetch(`http://localhost:3001/playlist/?readernameEN=${readername}`)
    .then((response)=> response.json())
    .then((result) => 
            {
              result = result.map(p => 
              { 
                if(p.surahEN==surahname)return p
              });
              let SETVar = new Set(result);
              SETVar.delete(undefined);
              setData(Array.from(SETVar))
            }
        ) 
  } 
  
  return (
    <>
    {data.length>0 ?data.map((item)=>{
      return(
        <div className='container' key={item.id}>
          <div className='Audioplay'>
            <div className='col-xl-6'>
              <div className='audiobox text-center'>
                <div className='titleoverlay'>{item.surahAR}</div>
              </div>
            </div>
            <div className='col-xl-6'>
              <Slider readername={item.readernameAR} surahname={item.surahAR}/>
            </div>
            <div className='col-xl-6'>
              <Audio audiosrc={item.surahaudio}/>
            </div>
          </div>
        </div>
      )
    }):<NotFoundPage/>}
   
    </>
  )
}
