import React, { useState } from "react";
import Playlist from "../pages/Playlist";

function Search (){
  let [searchkey , setSearchKey] = useState('empty');
    const handleInput = (e) =>{
      setSearchKey(e.target.value)
    }
    return (
     <>
      <div className="searchbox">
        <input type="text" className="form-control" onInput={handleInput} placeholder="Enter surah name "/>
      </div>
      <Playlist searchkey = {searchkey}/>
     </>
    )
}
export default Search
