import $ from "jquery"
export default function Pagination(props) {
  const limitPerPage = 8;
  const paginationSize = 7;
  let [itemnums , setItemNums] = useState();
  let [numofpages, setNumOfPages] = useState(0);
  let [curre , setLimitPerPage] = useState();
  useEffect(()=>{
    setItemNums(props.datalength);
    setNumOfPages(Math.ceil(props.datalength/limitPerPage));

  } , [])
  return (
    <>
    <h1>{numofpages}</h1>
    <nav aria-label="...">
      <ul className="pagination">
        <li className="page-item disabled">
          <a className="page-link text-dark">Previous</a>
        </li>
        {/* <li className="page-item"><a className="page-link" href="#">1</a></li>
