import React, { useEffect, useState } from 'react'
import { useParams , Link } from 'react-router-dom';
import Card from '../components/Card';

export default function Playlist() {
  let [data , setData] = useState([]);
  let {readername} = useParams();
  let [searchword , setSearchWord] = useState('');
  let founditems;
  let getData = ()=>{
      fetch(`http://localhost:3001/playlist/?readernameEN=${readername}`)
      .then((response)=> response.json()).
      then((result) => setData(result)) 
  }
  useEffect(()=>{
    getData();
    founditems = data.length;
  },[readername])

  if(data.length > 1){
    data.sort((a ,b) => a.surahnum- b.surahnum)
  }

  let handleChange = (e)=>{
      setSearchWord(e.target.value)
  }
  return (
    <>
    <div className='container'>
      <div className="searchbox">
        <input type="text" className="form-control" onChange={handleChange} placeholder="Enter surah name "/>
      </div>
      <div className='cards-container row'>
        {data.map((Item)=>{
          let surahname = Item.surahEN;
          if(surahname.toLowerCase().includes(searchword.trim())){
            return(
              <div className='col-xl-3 pt-5' key={Item.surahEN}>
                <Link to={`/playlist/${Item.readernameEN}/${Item.surahEN}`}>
                  <Card data={Item}/>
                </Link>
              </div>
            )
          }
        })
        }
      </div>
      <div className='pagination'>
      <nav aria-label="...">
  <ul class="pagination">
    <li class="page-item disabled">
      <a class="page-link">Previous</a>
    </li>
    <li class="page-item"><a class="page-link" href="#">1</a></li>
    <li class="page-item active" aria-current="page">
      <a class="page-link" href="#">2</a>
    </li>
    <li class="page-item"><a class="page-link" href="#">3</a></li>
    <li class="page-item">
      <a class="page-link" href="#">Next</a>
    </li>
  </ul>
</nav>
      </div>
    </div>
    </>
  )
}
