import React, { useEffect, useState } from 'react'
import { useParams , Link } from 'react-router-dom';
import Card from '../components/Card';
import Pagination from '../components/Pagination';

export default function Playlist() {
  let [data , setData] = useState([]);
  let {readername} = useParams();
  let [temp] = useState(new Set());
  let [searchword , setSearchWord] = useState('');
  let getData = ()=>{
      fetch(`http://localhost:3001/playlist/?readernameEN=${readername}`)
      .then((response)=> response.json()).
      then((result) => setData(result)) 
  }
  useEffect(()=>{
    getData();
  },[readername])

  if(data.length > 1){
    data.sort((a ,b) => a.surahnum- b.surahnum)
  }

  let handleChange = (e)=>{
    temp.clear()
    setSearchWord(e.target.value);
  }
  return (
    <>
    <div className='container'>
      <div className="searchbox">
        <input type="text" className="form-control" onKeyDown={handleChange} onChange={handleChange} placeholder="Enter surah name "/>
      </div>
      <div className='cards-container row'>
        {
        data.map((Item,index,Array)=>{
          let surahname = Item.surahEN;
          if(surahname.toLowerCase().includes(searchword.trim())){
            temp.add([Item])
            return(
              <div className='col-xl-3 pt-5' key={Item.surahEN}>
                <Link to={`/playlist/${Item.readernameEN}/${Item.surahEN}`}>
                  <Card data={Item}/>
                </Link>
              </div>
            )
          }
        })
        }
      </div>
      {console.log(temp)}
      {temp.size > 0&&<div className='pagination py-0 justify-content-center'><Pagination datalength={temp.size}/></div>}
      
    </div>
    </>
  )
}
