import React , {useState , useEffect} from 'react'
import { Link } from 'react-router-dom'

export default function Sidebar() {
    let [data , setData] = useState([]);
    
    let getData = () => {
        fetch("http://localhost:3000/playlist").
        then((response) => response.json()).
        then((result) => {
            result = result.map(p => p.reader)
            let SETVar = new Set(result);
            setData(Array.from(SETVar));
        })
    }
    useEffect(()=>{
        getData();
    } , [])
  

    return (
        <div className="Sidebar">
        <div className='Title'>
            <Link to="/">
                <div className='row'>
                    <div className='col-xl-12 col-sm-12'>
                        <h2>Quran Kareem</h2>
                    </div>
                    <div className='col-xl-4 col-sm-6 m-auto'>
                        <img src="https://www.svgrepo.com/show/71049/quran-rehal.svg" alt=""/>
                    </div>
                    
                </div>    
            </Link>
        </div>
        <div className='readerlist'>
            <ul className='list-unstyled p-0'>
                {data.map((Item)=>{
                    return(
                        <li className='reader' key={Item}>
                            <Link to="playlist" prop>{Item}</Link>
                        </li>
                    );
                })}
                
            </ul>
        </div>
        </div>
    )
}
