import React, { useState , useEffect, useRef} from 'react'
import $ from 'jquery'
const Audio = (props) => {
    const root = document.documentElement; 
    const audioplayer = useRef(); 
    const progressbar = useRef(); 
    const animationRef = useRef();

    const [isPlaying, setIsPlaying] = useState(false);
    const [duration , setDuration] = useState(0); 
    const [currenttime , setCurrentTime] = useState(0) 
   

    useEffect(()=>{ 
        if(props.audiosrc.readyState > 0)
        {
            const seconds = Math.floor(audioplayer.current.duration) 
            setDuration(seconds) 
            progressbar.current.max = seconds;
            displayBufferedAmount();
        }
        else{
            const audio = document.querySelector("audio");
            audio.addEventListener("loadedmetadata",()=>{
                const seconds = Math.floor(audioplayer.current.duration) 
                setDuration(seconds)            
                progressbar.current.max = seconds;
                displayBufferedAmount(audio);
            })
        }
    },[audioplayer?.current?.loadedmetadata, audioplayer?.current?.readyState])
    const displayBufferedAmount = (audio) => {
        const bufferedAmount = Math.floor(audio.buffered.end(audio.buffered.length - 1));
        root.style.setProperty('--buffered-width', `${(bufferedAmount / duration) * 100}%`);
    }
    const counttime = (secs)=>{
        console.log(secs)
        const minutes = Math.floor(secs/60);
        const seconds = Math.floor(secs%60);
        const returnedSeconds = seconds < 10 ? `0${seconds}` : `${seconds}`;
        return `${minutes}:${returnedSeconds}`
    }
    let showRangeProgress = (rangeInput) => {
        root.style.setProperty('--volume-before-width', rangeInput.value / rangeInput.max * 100 + '%');
    }
    const handleClick = (e) => {
        const prevState = isPlaying;
        setIsPlaying(!prevState);
        const playbtn = document.getElementById("play-icon");
        if(!prevState) {
          audioplayer.current.play();
          playbtn.classList.replace("play" , "pause");
          playbtn.innerHTML = `<i class="fa-solid fa-pause"></i>`;
          animationRef.current = requestAnimationFrame(whilePlaying)
        } else {
          audioplayer.current.pause();
          playbtn.classList.replace("pause" , "play")
          playbtn.innerHTML = `<i class="fa-solid fa-play"></i>`
          cancelAnimationFrame(animationRef.current);
        }
    }

    const whilePlaying = () => {
        progressbar.current.value = audioplayer.current.currentTime;
        changePlayerCurrentTime();
        animationRef.current = requestAnimationFrame(whilePlaying);
    }
    
    const handleChange = (e) => {
        audioplayer.current.currentTime = progressbar.current.value;
        root.style.setProperty("--seek-before-width" ,`${progressbar.current.value / duration * 100}%`);
        setCurrentTime(progressbar.current.value);
    }

    const changePlayerCurrentTime = () => {
        root.style.setProperty('--seek-before-width', `${progressbar.current.value / duration * 100}%`);
        setCurrentTime(progressbar.current.value);
    }

    const handlemouseover = () => {
        $("#volume-slider").fadeIn()
    }

    const handlemouseleave = () => {
        $("#volume-slider").fadeOut()
    }
    return (
        <>
            <div className='audiocontroller'>
                <audio ref={audioplayer} src={props.audiosrc} preload="metdata"></audio>
                <div className='row align-items-center justify-content-center'>
                    <div className='col-xl-1 col-sm-2'>
                        <button id='play-icon' className="play btn"onClick={handleClick}><i className="fa-solid fa-play"></i></button>
                    </div>
                    <div className='col-xl-1 col-sm-1'>
                        <span  className='time' >{counttime(currenttime)}</span>
                    </div>
                    <div className='col-xl-8 col-sm-9 '>
                        <input type="range"  onChange={handleChange}  ref={progressbar} defaultValue="0" />
                    </div>
                    <div className='col-xl-1 col-sm-1'>
                        <span className='time' >{counttime(duration)}</span>
                    </div>
                    <div className='col-xl-1 col-sm-3'onMouseOver={handlemouseover} onMouseLeave={handlemouseleave}>
                        <input type="range" className='soundcontrolleroverlay'  id="volume-slider" max="100" defaultValue="100"/>
                        <button className='unmute btn' ><i className="fa-solid fa-volume-high text-white"></i></button>
                    </div>
                    
                </div>
            </div>
        </>
    )
}

export default Audio;
