import React , { useEffect, useState } from 'react'
import $ from "jquery"
export default function Pagination(props) {
  const [limitPerPage] = useState(8);
  const paginationSize = 7;
  let [itemnums , setItemNums] = useState(0);
  let [numofpages, setNumOfPages] = useState(0);
  let [currentpage , setCurrentPages] = useState(0);
  useEffect(()=>{
    setItemNums(props.datalength);
    setNumOfPages(Math.ceil(props.datalength/limitPerPage));
  } , [])
  let returnpageitems = (prop) => {
    // console.log(prop)
    for(const i = 1 ; i <= prop ; i++){
      const l = ``
      return(
      <li className="page-item">
        <a className="page-link text-dark" href="#">{i}</a>
      </li>
      )
    }
  }
  return (
    <>
    <h1>{numofpages}</h1>
    <nav aria-label="...">
      <ul className="pagination">
        <li className="page-item disabled">
          <a className="page-link text-dark">Previous</a>
        </li>
        {numofpages>0? returnpageitems(numofpages):null}
        <li className="page-item">
          <a className="page-link text-dark" href="#">Next</a>
        </li>
      </ul>
    </nav>
    </>
  )
}
