import React, { useState } from 'react'

export default function Audio(props) {
    let [audiotag , setAudioTag] = useState('');
    let [playbtn , setPlayBtn] = useState('');
    let [seekslidertag , setSeekSliderTag] = useState('');
    let [durationtag , setDurationTag] = useState('')

    const root = document.documentElement;
    const counttime = (secs)=>{
        let minutes = Math.floor(secs/60);
        let seconds = Math.floor(secs%60);
        const returnedSeconds = seconds < 10 ? `0${seconds}` : `${seconds}`;
        return `${minutes}:${returnedSeconds}`
    }
    let displayAudioDuration = (audioduratiom)=>{
        const durationbox = document.getElementById("duration");
        durationbox.textContent = counttime(audioduratiom)
    }
    let setSliderMax = (audio) => {
        const seekSlider = document.getElementById('seek-slider');
        seekslidertag.max = Math.floor(audio.duration);
    }
    const handleloading =()=>{
        const audio = document.querySelector("audio");
        const playbtn = document.getElementById("play-icon");
        const seekSlider = document.getElementById('seek-slider');
        
        setAudioTag(audio);
        setPlayBtn(playbtn);
        setSeekSliderTag(seekSlider);

        const bufferedAmount = audio.buffered.end(audio.buffered.length - 1);
        root.style.setProperty("--buffered-width" , bufferedAmount + "px")
        displayAudioDuration(audio.duration)
        setSliderMax(audio);
    }
    console.log(audiotag)
    const handleInput = () => {

    }
    const handleClick = (e) => {
        if(playbtn.classList.contains('play')) {
          audiotag.play();
          // playAnimation.playSegments([14, 27], true);
          playbtn.innerHTML = `<i class="fa-solid fa-pause"></i>`
          playbtn.classList.replace('play',"pause");
        //   requestAnimationFrame(whilePlaying);
        } else {
          audiotag.pause();
          // playAnimation.playSegments([0, 14], true);
          playbtn.innerHTML = `<i class="fa-solid fa-play"></i>`
          playbtn.classList.replace('pause',"play");
        //   cancelAnimationFrame(rAF);
        }
    }
    const handleChange = () => {
        audiotag.currentTime = seekslidertag.value;
        /* if(!audiotag.paused) {
        requestAnimationFrame(whilePlaying);
        } */
    }
    const handleTimeUpdate = () => {
        seekslidertag.value = Math.floor(audiotag.currentTime);
    }
    return (
        <div>
            <div className='audiocontroller p-5'>
                <div className='row align-items-center justify-content-center'>
                    <div className='col-xl-2 col-sm-2'>
                        <button id='play-icon' className='play btn' onClick={handleClick}><i className="fa-solid fa-play"></i></button>
                    </div>
                    <div className='col-xl-1 col-sm-1'>
                        <span id='current-time' className='time' >0:00</span>
                    </div>
                    <div className='col-xl-4 col-sm-8 ml-2'>
                        <input type="range" onInput={handleInput} id='seek-slider' onChange={handleChange} max="100" value="0" />
                    </div>
                    <div className='col-xl-1 col-sm-1'>
                        <span id='duration' className='time' >0:00</span>
                    </div>
                    <div className='col-xl-2 col-sm-2'>
                        <div className='volumeoverlay'>
                            <input type="range" className='soundcontrolleroverlay'  id="volume-slider" max="100" value="100"/>
                        </div>
                        <button className='unmute btn'><i className="fa-solid fa-volume-high text-white"></i></button>
                    </div>
                    <audio src={props.audiosrc} onLoadedMetadata={handleloading} preload="metdata" controls onTimeUpdate={handleTimeUpdate}/>
                </div>
            </div>
        </div>
    )
}
