import React, { useEffect, useState } from 'react'
import { useParams , Link } from 'react-router-dom';
import Card from '../components/Card';
import Search from './../components/Search';

export default function Playlist(props) {
  let [data , setData] = useState([]);
  let {readername} = useParams();
  let [searchword , setSearchWord] = useState('')
  let getData = ()=>{
      fetch(`http://localhost:3001/playlist/?readernameEN=${readername}`)
      .then((response)=> response.json()).
      then((result) => setData(result)) 
  }
  
  
  useEffect(()=>{
    getData();
  },[readername])

  if(data.length > 1){
    data.sort((a ,b) => a.surahnum- b.surahnum)
  }

  let handleChange = (e)=>{
      setSearchWord(e.target.value)
  }
  let handleClick = () => {
    console.log(props);
    // props.fun()
  }
 
  return (
    <>
    <div className='container'>
      <div className="searchbox">
        <button onClick={handleClick}>press</button>
      </div>
      <div className='row'>
      
        {data.map((Item)=>{
          let surahname = Item.surahEN;
          if(surahname.toLowerCase().includes(searchword.trim())){
            return(
              <div className='col-xl-3 pt-5' key={Item.surahEN}>
                <Link to={`/playlist/${Item.readernameEN}/${Item.surahEN}`}>
                  <Card data={Item}/>
                </Link>
              </div>
            )
          }
        })}
      </div>
    </div>
    </>
  )
}
