import React, { useEffect, useState } from 'react'
import {$} from 'jquery'
export default function Slider(props) {
  let [parawidth , setParaWidth] = useState(0);
  let [sliderwidth , setSliderWidth] = useState(0);
  let root = document.documentElement;
  useEffect(()=>{
    let sliderpara = document.querySelector(".slider p")
    let slider = document.querySelector(".slider")
    setSliderWidth(slider);
    setParaWidth(sliderpara.offsetWidth);

    
  } , []);
  root.style.setProperty("--slider-width" , parawidth + "px");
  root.style.setProperty("--window-width" , slider.innerWidth + "px");
  return (
    <>
    {console.log(sliderwidth)}
    <div className="slider bg-dark overflow-hidden">
      <p className='text-center '>سورة { props.surahname } بصوت القارئ { props.readername }</p> 
    </div>
    </>
  )
}
