import React from "react";
import Playlist from "../pages/Playlist";

function Search (){
    const handleInput = (e) =>{
      return(
        <Playlist searchkey = {e.target.value}/>
      )
      console.log("the new input" + e.target.value)
    }
    return (
      <div className="searchbox">
        <input type="text" className="form-control" onInput={handleInput} placeholder="Enter surah name "/>
      </div>
    )
}
export default Search
