import React , { useEffect, useState } from 'react'
import $ from "jquery"
export default function Pagination(props) {
  const [limitPerPage] = useState(4);
  const paginationSize = 7;
  let [itemnums , setItemNums] = useState(0);
  let [numofpages, setNumOfPages] = useState(0);
  let [currentpage , setCurrentPages] = useState(1);
  useEffect(()=>{
    setItemNums(props.datalength);
    setNumOfPages(Math.ceil(props.datalength/limitPerPage));
  } , [props.datalength])

  const handleClick = (e) =>{
    setCurrentPages(e.target.textContent)
  }

  const moveNext = () => {
    setCurrentPages(currentpage + 1)
  }

  const movePrev = () => {
    setCurrentPages(currentpage - 1)

  }
  function getPageList (totalPages,page,maxLength){
    function range (start,end){
        return Array.from(Array(end-start+1), ( _ , i) => i + start);
    }
    var sideWidth = maxLength < 9 ? 1 : 2; //
    var leftWidth = (maxLength - sideWidth * 2 - 3) >> 1; //
    var rightWidth = (maxLength - sideWidth * 2 - 3) >> 1; //
    if(totalPages <= maxLength){
        return range(1,totalPages);
    }
    if(page <= maxLength - sideWidth - 1 - rightWidth){
        return range(1, maxLength - sideWidth -1).concat(0 , range(totalPages - sideWidth +1 , totalPages));
    }

    if(page >= totalPages - sideWidth -1 - rightWidth){
        return range(1, sideWidth ). concat(0 , range(totalPages - sideWidth - 1 - rightWidth - leftWidth, totalPages));
    }

    return range(1,sideWidth).concat(0,range(page - leftWidth , page + rightWidth) , 0 , range(totalPages - sideWidth +  1 , totalPages));
}
function pagination() {
    var numberOfItems = $(".card").length;
    var limitPerPage = 6 ;
    var totalPages = Math.ceil(numberOfItems / limitPerPage);
    var paginationSize = 7;//
    var currentPage;
    function showPage (whichPage){
        if(whichPage < 1 || whichPage > totalPages) return false;

        currentPage = whichPage;
        window.scroll({
            top:0,
            behavior:"smooth"
        })
        $(".card").hide().slice((currentPage - 1 ) * limitPerPage, currentPage * limitPerPage).show()

        $(".pagination li").slice(1,-1).remove(); //

        getPageList(totalPages , currentPage , paginationSize).forEach(item => {
            $("<li>").addClass("page-item").addClass(item ? "current-page" : "dots")
            .toggleClass("active", item === currentPage).append($("<a>").addClass("page-link")
            .attr({href:"javascript:void(0)"}).append($("<span>").text(item))).insertBefore(".next-page");
        });
        $(".previous-page").toggleClass("disable", currentPage === 1);
        $(".next-page").toggleClass("disable", currentPage === totalPages);
        return true;
    }
   $(".pagination").append(
        $("<li>").addClass("page-item").addClass("previous-page").append($("<a>").addClass("page-link").attr({href: "javascript:void(0)"}).append($("<i>").addClass("fa-solid fa-chevron-left"))),
        $("<li>").addClass("page-item").addClass("next-page").append($("<a>").addClass("page-link").attr({href: "javascript:void(0)"}).append($("<i>").addClass("fa-solid fa-chevron-right")))
    );
    $(".card-content").show();
    showPage(1);
    $(document).on("click" , ".pagination li.current-page:not(.active)", function (){
        return showPage(+$(this).text())
    })
    $(".next-page").on("click", function(){
        return showPage(currentPage + 1);
    })

    $(".previous-page").on("click", function(){
        return showPage(currentPage - 1);
    })
};
  const returnpageitems = (prop) => {
    console.log(prop)
    const numlist = []
    for(let i = 1 ; i <= prop ; i++) numlist.push(i);
    return(numlist.map((num) => {
      $(".card").hide().slice((currentpage - 1 ) * limitPerPage, currentpage * limitPerPage).show()
      return(
        <li className="page-item active" key={num} onClick={handleClick}>
          <a className="page-link text-dark rounded-circle mx-1" href="#">{num}</a>
        </li>
        )
    })) 
  }

  return (
    <>
    <nav aria-label="...">
      <ul className="pagination">
        <li className="page-item">
          <a className=" btn btn-light text-dark mr-2 rounded-circle" onClick={movePrev}><i className="fa-solid fa-angles-left"></i></a>
        </li>
        {numofpages>0?returnpageitems(numofpages):null}
        <li className="page-item">
          <a className="btn btn-light text-dark ml-2 rounded-circle" onClick={moveNext}><i className="fa-solid fa-angles-right"></i></a>
        </li>
      </ul>
    </nav>
    </>
  )
}
