import React, { useEffect, useState } from 'react'
import { useParams , Link } from 'react-router-dom';
import Card from '../components/Card';
import Search from '../components/Search';

export default function Playlist(props) {
    let [data , setData] = useState([]);
    let {readername} = useParams();

    let getData = ()=>{
        fetch(`http://localhost:3000/playlist/?readernameEN=${readername}`)
        .then((response)=> response.json()).
        then((result) => setData(result)) 
    }
    data.sort((a ,b) => a.surahnum- b.surahnum)
    
    useEffect(()=>{
      getData();
    },[readername])
    let handleChange = (e)=>{
     console.log()
      /* data.map((item)=>{
        let searchword = e.target.value
        console.log(item)
         if(item.surahEN.includes(searchword.trim().toLowerCase()) ){
          setData(item)
         }
      }) */
    }
  return (
    <>

    <div className='container'>
      <div className="searchbox">
        <input type="text" className="form-control" onChange={handleChange} placeholder="Enter surah name "/>
      </div>
      <div className='row'>
      
        {data.map((Item)=>{
            return(
                <div className='col-xl-3 pt-5' key={Item.surahEN}>
                  <Link to="/">
                    <Card data={Item}/>
                  </Link>
                </div>
            )
        })}
      </div>
    </div>
    </>
  )
}
