import React ,{useState , useEffect} from 'react'
import { useParams } from 'react-router-dom';
import lottieWeb from 'https://cdn.skypack.dev/lottie-web';
export default function PlayAudio() {
  let {readername,surahname} = useParams();
  let [data, setData] = useState([]);
  let getData = ()=>{
    fetch(`http://localhost:3000/playlist/?readernameEN=${readername}`)
    .then((response)=> response.json()).
    then((result) => 
            {
              result = result.map(p => 
              { 
                if(p.surahEN==surahname)
                return p
              });
              let SETVar = new Set(result);
              SETVar.delete(undefined);
              setData(Array.from(SETVar))
            }
        ) 
  } 
  useEffect(()=>{
    getData();
  } , [])
  let handleClick = ()=>{
    
    console.log("Hello in click event")
  }
  const seekSlider = document.getElementById('seek-slider');

  const setSliderMax = (audio) => {
    seekSlider.max = Math.floor(audio.duration);
  }
  let handleloading =()=>{
    console.log("Hello in load event");
    let audio = document.querySelector("audio");
    displayAudioDuration(audio.duration)
    setSliderMax(audio);
    
  }
  let counttime = (secs)=>{
    let minutes = Math.floor(secs/60);
    console.log(minutes) 
    let seconds = Math.floor(secs%60);
    console.log(seconds)
    const returnedSeconds = seconds < 10 ? `0${seconds}` : `${seconds}`;
    console.log(returnedSeconds)
    return `${minutes}:${returnedSeconds}`
  }
  let displayAudioDuration = (audioduratiom)=>{
    let durationbox = document.getElementById("duration");
    durationbox.textContent = counttime(audioduratiom)
  }
  let handleChange = (e)=>{
    e.target.value = 0
  }
  return (
    <>
    
    {data.map((item)=>{
      return(
        <div className='container' key={item.id}>
          <div className='row align-items-center justify-contents-center'>
            <div className='col-xl-12'>
              <div className='audiobox text-center'>
              <div className='titleoverlay'>{item.surahAR}</div>
              </div>
            </div>
            <div className='col-xl-12'>
              <div className='audiocontroller p-5'>
                <button className='play-icon btn' onClick={handleClick}><i className="fa-solid fa-play"></i></button>
                <span id='current-time' className='time' >0:00</span>
                <input type="range" className='seek-slider' max="100" onChange={handleChange}/>
                <span id='duration' className='time' ></span>
                <div className='audiosoundcontroller'>
                  <div className='row'>
                    <div className='col-xl-12'><output>100</output></div>
                    <div className='col-xl-12'><input type="range" className='soundcontrolleroverlay'  id="volume-slider" max="100" value="0"/></div>
                    <div className='col-xl-12'><button className='unmute btn'><i className="fa-solid fa-volume-high"></i></button></div>
                  </div>
                </div>
                <audio src={item.surahaudio} onLoadedMetadata={handleloading} preload="metdata" loop />
              </div>

            </div>
          </div>
        </div>

      )
    })}
    </>
  )
}
