import React, { useState , useEffect} from 'react'
import lottieWeb from 'https://cdn.skypack.dev/lottie-web';

export default function Audio(props) {
    const playIconContainer = document.getElementById('play-icon');
    let [currnettime , setCurrentTime] = useState("0:00");
    // let [lottiefunc , setLottieFun] = useState("0:00");
    let [audioduration , setAudioDuration] = useState("0:00");
    let [audiostate , setAudioState] = useState("play");
    let [maxslider , setMaxSlider] = useState(100);
    let [slidervalue , setSliderValue] = useState(0);
    let raf = null;
    let playAnimation;
    useEffect(()=>{
            playAnimation = lottieWeb.loadAnimation({
            container: playIconContainer,
            path: 'https://maxst.icons8.com/vue-static/landings/animated-icons/icons/pause/pause.json',
            renderer: 'svg',
            loop: false,
            autoplay: false,
            name: "Play Animation",
        });
        // setLottieFun(playAnimation)
    },[])
    const showRangeProgress = (rangeInput) => {
        let seekslider = document.getElementById("seek-slider")
        let audioPlayerContainer = document.querySelector()
        if(rangeInput === seekslider) audioPlayerContainer.style.setProperty('--seek-before-width', rangeInput.value / rangeInput.max * 100 + '%');
        else audioPlayerContainer.style.setProperty('--volume-before-width', rangeInput.value / rangeInput.max * 100 + '%');
    }
    const root = document.documentElement;
    const counttime = (secs)=>{
        let minutes = Math.floor(secs/60);
        let seconds = Math.floor(secs%60);
        const returnedSeconds = seconds < 10 ? `0${seconds}` : `${seconds}`;
        return `${minutes}:${returnedSeconds}`
    }
    let displayAudioDuration = (audioduratiom)=>{
        setAudioDuration(counttime(audioduratiom)) 
    }
    let setSliderMax = (audio) => {
        setMaxSlider(Math.floor(audio.duration));
    }
    const displayBufferedAmount = () => {
        let audio =  document.querySelector("audio");
        let seekslider = document.getElementById("seek-slider")
        const bufferedAmount = Math.floor(audio.buffered.end(audio.buffered.length - 1));
        root.style.setProperty('--buffered-width', `${(bufferedAmount / seekslider.max) * 100}%`);
    }
    const whilePlaying = () => {
        let audio =  document.querySelector("audio");
        let seekslider = document.getElementById("seek-slider")
        seekslider.value = Math.floor(audio.currentTime)
        let currenttimebox = document.getElementById("current-time");
        currenttimebox.textContent = counttime(seekslider.value)
        root.style.setProperty('--seek-before-width', `${slidervalue / maxslider * 100}%`);
        raf = requestAnimationFrame(whilePlaying);
    }
    /* let audio =  document.querySelector("audio");
    if (audio.readyState > 0) {
        displayDuration();
        setSliderMax();
        displayBufferedAmount();
    } else {
        audio.addEventListener('loadedmetadata', () => {
            displayDuration();
            setSliderMax();
            displayBufferedAmount();
        });
    } */
    
    const handleloading =()=>{
        const audio = document.querySelector("audio");
        const bufferedAmount = audio.buffered.end(audio.buffered.length - 1);
        root.style.setProperty("--buffered-width" , bufferedAmount + "px")
        displayAudioDuration(audio.duration)
        setSliderMax(audio);
    }
    const handleInput = (e) => {
        showRangeProgress(e.target);
        let seekslider = document.getElementById("seek-slider");
        let currenttimebox = document.getElementById("current-time");
        currenttimebox.textContent = counttime(seekslider.value)
        const audio = document.querySelector("audio");  
        if(!audio.paused) {
          cancelAnimationFrame(raf);
        }
    }
    const handleClick = (e) => {
        const audio = document.querySelector("audio");
        const playbtn = document.getElementById("play-icon");
        if(audiostate === 'play') {
          audio.play();
          setAudioState("pause");
          playAnimation.playSegments([14, 27], true);
          playbtn.innerHTML = `<i class="fa-solid fa-pause"></i>`
          requestAnimationFrame(whilePlaying);
        } else {
          audio.pause();
          setAudioState("play");
          playAnimation.playSegments([0, 14], true);
          playbtn.innerHTML = `<i class="fa-solid fa-play"></i>`
          cancelAnimationFrame(raf);
        }
    }
    const handleChange = () => {
        let seekslider = document.getElementById("seek-slider");
        const audio = document.querySelector("audio");
        audio.currentTime = seekslider.value
        if(!audio.paused) {
            requestAnimationFrame(whilePlaying);
        }
    }
    
    const handleTimeUpdate = () => {
        const audio = document.querySelector("audio");
        const seekSlider = document.getElementById('seek-slider');
        seekSlider.value = Math.floor(audio.currentTime);
    }
    return (
        <>
            <div className='audiocontroller p-5'>
                <div className='row align-items-center justify-content-center'>
                    <div className='col-xl-2 col-sm-2'>
                        <button id='play-icon' className={`${audiostate} btn`} onClick={handleClick}><i className="fa-solid fa-play"></i></button>
                    </div>
                    <div className='col-xl-1 col-sm-1'>
                        <span id='current-time' className='time' >0:00</span>
                    </div>
                    <div className='col-xl-4 col-sm-8 ml-2'>
                        <input type="range" onInput={handleInput} id='seek-slider' onChange={handleChange} max={maxslider} defaultValue={slidervalue} />
                    </div>
                    <div className='col-xl-1 col-sm-1'>
                        <span id='duration' className='time' >{audioduration}</span>
                    </div>
                    <div className='col-xl-2 col-sm-2'>
                        <div className='volumeoverlay'>
                            <input type="range" className='soundcontrolleroverlay'  id="volume-slider" max="100" defaultValue="100"/>
                        </div>
                        <button className='unmute btn'><i className="fa-solid fa-volume-high text-white"></i></button>
                    </div>
                    <audio src={props.audiosrc} onLoadedMetadata={handleloading} preload="metdata" controls onTimeUpdate={handleTimeUpdate}/>
                </div>
            </div>
        </>
    )
}
