import React, { useEffect, useState } from 'react'
import { useParams , Link } from 'react-router-dom';
import $ from "jquery"
import Card from '../components/Card';
import Pagination from '../components/Pagination';
import NotFoundPage from './NotFound';

export default function Playlist() {
  let [data , setData] = useState([]);
  let {readername} = useParams();
  let [temp] = useState(new Set());
  let [searchword , setSearchWord] = useState('');
  let getData = ()=>{
      fetch(`http://localhost:3001/playlist/?readernameEN=${readername}`)
      .then((response)=> response.json()).
      then((result) => setData(result)) 
  }
  useEffect(()=>{
    getData();
/*     $("#searchword").value = "";
    setSearchWord('') */
  },[readername])

  if(data.length > 1){
    data.sort((a ,b) => a.surahnum- b.surahnum)
  }

  let handleChange = (e)=>{
    temp.clear()
    setSearchWord(e.target.value);
  }
  return (
    <>
    <div className='container'>
      <div className="searchbox">
        <input type="text" id="searchword" className="form-control"onChange={handleChange} placeholder="Enter surah name "/>
      </div>
      <div className='cards-container row'>
        {
    
        temp.size > 0 ? <div className='pagination py-0 justify-content-center'><Pagination datalength={temp.size}/></div>:<NotFoundPage/>
        }
      </div>
      

      
    </div>
    </>
  )
}
