import React , {useState , useEffect} from 'react'
import { Link } from 'react-router-dom'

export default function Sidebar() {
    let [data , setData] = useState([]);
    let getData = () => {
        fetch("http://localhost:3001/playlist?").
        then((response) => response.json()).
        then((result) => {
            setData(result.map(p => p.reader));
        })
    }
    useEffect(()=>{
        getData();
    } , [])
  

    return (
        <div className="Sidebar">
        <div className='Title'>
            <div className='row'>
                <div className='col-xl-6 col-sm-12'>
                    <Link to="/"><h2>Quran Kareem</h2></Link>
                </div>
                <div className='col-xl-6 col-sm-6 m-auto'>
                    <img src="https://upload.wikimedia.org/wikipedia/commons/thumb/6/64/Quran_Kareem.svg/1200px-Quran_Kareem.svg.png" alt=""/>
                </div>
                
            </div>
            
            
        </div>
        <div className='readerlist'>
            <ul>
                {data.map((Item)=>{
                    return(
                        <li className='reader' key={Item}>
                            <Link to="/">{Item}</Link>
                        </li>
                    );
                })}
                
            </ul>
        </div>
        </div>
    )
}
