import React , {useState , useEffect} from 'react'
import { Link } from 'react-router-dom'

export default function Sidebar() {
    let [data , setData] = useState([]);
    
    let getData = () => {
        fetch("http://localhost:3001/playlist").
        then((response) => response.json()).
        then((result) => {
            result = result.map(p => p.readernameEN)
            let SETVar = new Set(result);
            setData(Array.from(SETVar));
        })
    }
    useEffect(()=>{
        getData();
    } , [])
  

    return (
        <div className="Sidebar navbar-expand-lg display-flex">
            <div className='Title'>
                <Link to="/">
                    <div className='row align-items-center'>
                        <div className='col-xl-12 col-sm-6'>
                            <h2>Quran Kareem</h2>
                        </div>
                        <div className='col-xl-4 col-sm-4 m-xl-auto mr-sm-auto'>
                            <div className='site-logo'>
                                <img src="https://www.svgrepo.com/show/71049/quran-rehal.svg" alt=""/>
                            </div>
                        </div>
                        
                    </div>    
                </Link>
            </div>
            <button className='navbar-toggler text-white' type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation"><i class="fa-solid fa-bars"></i></button>
            <div className="readerlist collapse navbar-collapse" id="navbarSupportedContent">
                <ul className='list-unstyled p-0'>
                    {data.map((Item)=>{
                        return(
                            <li className='reader' key={Item}>
                                <Link to={`playlist/${Item}`}>{Item}</Link>
                            </li>
                        );
                    })}
                    
                </ul>
            </div>
        </div>
    )
}
