import React ,{useState , useEffect} from 'react'
import { useParams } from 'react-router-dom';

export default function PlayAudio() {
  let {readername,surahname} = useParams();
  let [data, setData] = useState([]);
  let getData = ()=>{
    fetch(`http://localhost:3000/playlist/?readernameEN=${readername}`)
    .then((response)=> response.json()).
    then((result) => 
            {
              result = result.map(p => 
              { 
                p.surahEN==surahname&&p
              });
              let SETVar = new Set(result);
              console.log(SETVar)
              setData(Array.from(SETVar))
            }
        ) 
} 
useEffect(()=>{
  getData()
} , [])
// console.log(data)
  return (
    <div>
      <h1>hello in play audio {surahname}</h1>
    </div>
  )
}
