import React, { useState , useEffect} from 'react'
import $ from 'jquery'
export default function Audio(props) {
    let raf = null;
    const root = document.documentElement;
    let seekslider = document.getElementById("seek-slider");
    let audio = document.querySelector("audio");        
    let playbtn = document.getElementById("play-icon");
    useEffect(()=>{
        let audio = document.querySelector("audio");
        if (audio.readyState > 0) {
            let audio = document.querySelector("audio");
            displayAudioDuration(audio.duration);
            setSliderMax(audio);
            displayBufferedAmount();
        } 
        else {
            let audio = document.querySelector("audio");
            audio.addEventListener("loadeddata",()=>{
                const audio = document.querySelector("audio");
                const bufferedAmount = audio.buffered.end(audio.buffered.length - 1);
                root.style.setProperty("--buffered-width" , bufferedAmount + "px")
                displayAudioDuration(audio.duration)
                displayBufferedAmount()
                setSliderMax(audio);
            })
        } 
        let displayAudioDuration = (audioduratiom)=>{
        let duration = document.getElementById("duration");
        duration.textContent = counttime(audioduratiom)
    } 
    let setSliderMax = (audio) => {
        let slidermax = document.getElementById("seek-slider");
        slidermax.max = Math.floor(audio.duration)
    }
          
    },[])
    //
    const showRangeProgress = (rangeInput) => {
        let seekslider = document.getElementById("seek-slider")
        let audioPlayerContainer = document.querySelector(".audiocontroller")
        if(rangeInput === seekslider) root.style.setProperty('--seek-before-width', rangeInput.value / rangeInput.max * 100 + '%');
        else root.style.setProperty('--volume-before-width', rangeInput.value / rangeInput.max * 100 + '%');
    }
    //sure
    const counttime = (secs)=>{
        let minutes = Math.floor(secs/60);
        let seconds = Math.floor(secs%60);
        const returnedSeconds = seconds < 10 ? `0${seconds}` : `${seconds}`;
        return `${minutes}:${returnedSeconds}`
    }
    //
    const displayBufferedAmount = () => {
        console.log("buffered amount")
        let audio =  document.querySelector("audio");
        let seekslider = document.getElementById("seek-slider")
        const bufferedAmount = Math.floor(audio.buffered.end(audio.buffered.length - 1));
        root.style.setProperty('--buffered-width', `${(bufferedAmount / seekslider.max) * 100}%`);
    }
    //
    const whilePlaying = () => {
        let audio =  document.querySelector("audio");
        let seekslider = document.getElementById("seek-slider")
        seekslider.value = Math.floor(audio.currentTime)
        let currenttimebox = document.getElementById("current-time");
        currenttimebox.textContent = counttime(seekslider.value)
        root.style.setProperty('--seek-before-width', `${seekslider.value / seekslider.max * 100}%`);
        raf = requestAnimationFrame(whilePlaying);
    }
    //
    const handleprogress = () => {
        displayBufferedAmount()
    }
    //
    const handlevolumeinput = (e) =>{
        showRangeProgress(e.target);
    }
    //
    const handleInput = (e) => {
        showRangeProgress(e.target);
        let seekslider = document.getElementById("seek-slider");
        let currenttimebox = document.getElementById("current-time");
        currenttimebox.textContent = counttime(seekslider.value)
        const audio = document.querySelector("audio");  
        if(!audio.paused) {
          cancelAnimationFrame(raf);
        }
    }
    //
    const handleClick = (e) => {

        if(playbtn.classList.contains("play")) {
          audio.play();
          playbtn.classList.replace("play" , "pause")
          playbtn.innerHTML = `<i class="fa-solid fa-pause"></i>`
          requestAnimationFrame(whilePlaying);
        } else {
          audio.pause();
          playbtn.classList.replace("pause" , "play")
          playbtn.innerHTML = `<i class="fa-solid fa-play"></i>`
          cancelAnimationFrame(raf);
        }
    }
    //
    const handleChange = () => {
        audio.currentTime = seekslider.value
        if(!audio.paused) {
            requestAnimationFrame(whilePlaying);
        }
    }
    /*------------------------------------------------------------------------------------------------------ */
    //done
    let handlemouseover = () => {
        $("#volume-slider").fadeIn()
    }
    //done
    let handlemouseleave = () => {
        $("#volume-slider").fadeOut()
    }
    return (
        <>
            <div className='audiocontroller p-5'>
                <div className='row align-items-center justify-content-center'>
                    <div className='col-xl-2 col-sm-2'>
                        <button id='play-icon' className="play btn"onClick={handleClick}><i className="fa-solid fa-play"></i></button>
                    </div>
                    <div className='col-xl-1 col-sm-1'>
                        <span id='current-time' className='time' >0:00</span>
                    </div>
                    <div className='col-xl-4 col-sm-8 ml-2'>
                        <input type="range" onInput={handleInput} id='seek-slider' onChange={handleChange} max="100" defaultValue="0" />
                    </div>
                    <div className='col-xl-1 col-sm-1'>
                        <span id='duration' className='time' >0:00</span>
                    </div>
                    <div className='col-xl-2 col-sm-2'onMouseOver={handlemouseover} onMouseLeave={handlemouseleave}>
                        <input type="range" className='soundcontrolleroverlay'  id="volume-slider" onInput={handlevolumeinput} max="100" defaultValue="100"/>
                        <button className='unmute btn' ><i className="fa-solid fa-volume-high text-white"></i></button>
                    </div>
                    <audio src={props.audiosrc} onProgress={handleprogress} preload="metdata" controls />
                </div>
            </div>
        </>
    )
}
